<?phpinclude_once("config.php");class DB{	private $hDB;	private $ResToArray;	private $Res;	public function __construct(){}	public function Connect($host,$user,$password,$db_name)	{					global $ERROR;					$db=mysql_connect($host,$user,$password);					if(!$db)					{						die($_ERROR['db']['connect']);					}					if(!mysql_select_db($db_name,$db))					{						die($_ERROR['db']['select']);					}					$this->hDB=$db;	}	public function Query($query)	{			global $ERROR;                        mysql_query ("set character_set_client='utf8'") || die(mysql_error());                        mysql_query ("set character_set_results='utf8'") || die(mysql_error());                        mysql_query ("set collation_connection='utf8'");			$res=mysql_query($query,$this->hDB);				if(!$res)				{					echo $_ERROR['db']['query'];				}				else				{					$this->Res=$res;				}	}	public function ResultToArray()	{		$mas=array();		while($row=mysql_fetch_assoc($this->Res))		{		$mas[]=$row;		}		$this->ResToArray=$mas;		return $mas;	}        public function ReturnRes()        {            $Res = mysql_fetch_row($this->Res);            return $Res[0];        }}?>